import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FaCheck, FaTimes, FaChevronDown } from 'react-icons/fa';

const DropdownMenu = ({ title, items }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="relative" onMouseEnter={() => setIsOpen(true)} onMouseLeave={() => setIsOpen(false)}>
      <button className="px-6 py-3 rounded-md text-lg font-medium bg-gray-700 text-gray-300 hover:bg-indigo-700 hover:text-white transition-colors duration-150 flex items-center">
        {title}
        <FaChevronDown className="ml-2 h-4 w-4" />
      </button>
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            transition={{ duration: 0.2 }}
            className="absolute left-0 mt-2 w-64 rounded-md shadow-lg bg-gray-800 ring-1 ring-black ring-opacity-5 p-4 z-20"
          >
            <div className="grid gap-3" role="menu" aria-orientation="vertical" aria-labelledby="options-menu">
              {items.map((item, index) => (
                <a
                  key={index}
                  href="#"
                  className="flex flex-col p-2 rounded-md hover:bg-gray-700 transition-colors border border-white border-opacity-10 hover:border-opacity-20"
                  role="menuitem"
                >
                  <div className="text-sm font-medium text-white">{item.title}</div>
                  <div className="text-xs text-gray-400 mt-1">{item.description}</div>
                </a>
              ))}
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

const FeatureRow = ({ feature, values }) => {
  return (
    <motion.div 
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className="grid grid-cols-7 gap-4 py-6 border-b border-gray-700 hover:bg-gray-800 transition-all duration-300"
    >
      <div className="font-medium text-gray-300 sticky left-0 bg-gray-900 text-lg flex items-center">{feature}</div>
      {values.map((value, index) => (
        <div key={index} className={`text-center flex items-center justify-center ${index === 2 ? 'bg-indigo-600 bg-opacity-20' : ''}`}>
          {typeof value === 'boolean' ? (
            value ? <FaCheck className="text-green-400 text-xl" /> : <FaTimes className="text-red-400 text-xl" />
          ) : (
            <span className="text-gray-300 font-medium">{value}</span>
          )}
        </div>
      ))}
    </motion.div>
  );
};

const PlanHeader = ({ plan, isAnnual, isPopular }) => {
  return (
    <div className={`text-center p-4 ${isPopular ? 'bg-indigo-600 bg-opacity-20' : ''}`}>
      {isPopular && (
        <span className="inline-block bg-indigo-600 text-white text-xs px-2 py-1 rounded-full uppercase font-semibold tracking-wide mb-2">
          Most Popular
        </span>
      )}
      <h3 className={`text-xl font-semibold ${isPopular ? 'text-indigo-400' : 'text-gray-300'}`}>{plan.name}</h3>
      <p className={`text-3xl font-bold mt-2 ${isPopular ? 'text-indigo-400' : 'text-indigo-400'}`}>
        {plan.price === "Custom" ? (
          "Custom"
        ) : (
          <>
            ${isAnnual ? plan.price : Math.round(plan.price * 1.2)}
            <span className="text-base font-normal text-gray-400">/mo</span>
          </>
        )}
      </p>
    </div>
  );
};

const Pricing = () => {
  const [isAnnual, setIsAnnual] = useState(false);

  const plans = [
    {
      name: "Starter VPS",
      price: 5,
      cpu: "1 vCPU",
      ram: "1 GB",
      storage: "25 GB SSD",
      transfer: "1 TB",
      backups: false,
      support: "Email",
    },
    {
      name: "Basic VPS",
      price: 10,
      cpu: "2 vCPU",
      ram: "2 GB",
      storage: "50 GB SSD",
      transfer: "2 TB",
      backups: true,
      support: "Email",
    },
    {
      name: "Pro VPS",
      price: 20,
      cpu: "2 vCPU",
      ram: "4 GB",
      storage: "80 GB SSD",
      transfer: "3 TB",
      backups: true,
      support: "24/7 Chat",
      isPopular: true,
    },
    {
      name: "Business VPS",
      price: 40,
      cpu: "4 vCPU",
      ram: "8 GB",
      storage: "160 GB SSD",
      transfer: "4 TB",
      backups: true,
      support: "24/7 Phone & Chat",
    },
    {
      name: "Enterprise VPS",
      price: 80,
      cpu: "8 vCPU",
      ram: "16 GB",
      storage: "320 GB SSD",
      transfer: "5 TB",
      backups: true,
      support: "Dedicated Team",
    },
    {
      name: "Custom VPS",
      price: "Custom",
      cpu: "Custom",
      ram: "Custom",
      storage: "Custom",
      transfer: "Custom",
      backups: true,
      support: "Tailored",
    },
  ];

  const whyUsItems = [
    { title: "Reliability", description: "99.9% uptime guarantee" },
    { title: "Performance", description: "High-speed SSD storage" },
    { title: "Support", description: "24/7 expert assistance" },
  ];

  const whyVPSItems = [
    { title: "Flexibility", description: "Customize resources as needed" },
    { title: "Scalability", description: "Easily upgrade as you grow" },
    { title: "Cost-effective", description: "Pay only for what you use" },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 text-white py-20 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <motion.h1 
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-5xl font-extrabold text-center mb-16"
        >
          Choose Your <span className="text-indigo-400">VPS Plan</span>
        </motion.h1>
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          className="flex flex-wrap justify-center items-center gap-4 mb-8"
        >
          <div className="flex">
            <button
              onClick={() => setIsAnnual(false)}
              className={`px-6 py-3 rounded-l-md text-lg font-medium ${
                !isAnnual ? 'bg-indigo-600 text-white' : 'bg-gray-700 text-gray-300'
              } transition-colors duration-150 hover:bg-indigo-700`}
            >
              Monthly
            </button>
            <button
              onClick={() => setIsAnnual(true)}
              className={`px-6 py-3 rounded-r-md text-lg font-medium ${
                isAnnual ? 'bg-indigo-600 text-white' : 'bg-gray-700 text-gray-300'
              } transition-colors duration-150 hover:bg-indigo-700`}
            >
              Annual (Save 20%)
            </button>
          </div>
          <div className="flex gap-4">
            <DropdownMenu title="Why Us" items={whyUsItems} />
            <DropdownMenu title="Why VPS" items={whyVPSItems} />
          </div>
        </motion.div>
        <div className="relative">
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            className="bg-gray-900 shadow-2xl rounded-lg overflow-hidden"
          >
            <div className="overflow-x-auto">
              <div className="inline-block min-w-full align-middle">
                <div className="overflow-hidden">
                  <div className="grid grid-cols-7 gap-4 bg-gray-800 sticky top-0 z-10">
                    <div className="font-semibold text-gray-300 text-xl p-8">Features</div>
                    {plans.map((plan, index) => (
                      <PlanHeader key={index} plan={plan} isAnnual={isAnnual} isPopular={plan.isPopular} />
                    ))}
                  </div>
                  <div className="divide-y divide-gray-700">
                    <FeatureRow feature="CPU" values={plans.map(p => p.cpu)} />
                    <FeatureRow feature="RAM" values={plans.map(p => p.ram)} />
                    <FeatureRow feature="Storage" values={plans.map(p => p.storage)} />
                    <FeatureRow feature="Transfer" values={plans.map(p => p.transfer)} />
                    <FeatureRow feature="Backups" values={plans.map(p => p.backups)} />
                    <FeatureRow feature="Support" values={plans.map(p => p.support)} />
                  </div>
                  <div className="grid grid-cols-7 gap-4 p-8 bg-gray-800">
                    <div></div>
                    {plans.map((plan, index) => (
                      <div key={index} className={`text-center ${plan.isPopular ? 'bg-indigo-600 bg-opacity-20 rounded-b-lg pb-4' : ''}`}>
                        <motion.button 
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          className={`w-full ${plan.isPopular ? 'bg-indigo-600 text-white' : 'bg-indigo-600 text-white'} rounded-md py-3 px-6 text-lg font-medium hover:bg-opacity-90 transition duration-150 ease-in-out`}
                        >
                          Select Plan
                        </motion.button>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default Pricing;
