import { ZapIcon, LayersIcon, ShieldIcon } from "lucide-react";
import React, { useEffect, useState, useRef } from "react";
import { render } from "react-dom";
import { motion } from "framer-motion";
const FeaturesSection = () => {
  const [activeFeature, setActiveFeature] = useState(0);
  const [activeCardTop, setActiveCardTop] = useState(0);
  const cardRefs = useRef([]);
  const features = [
    {
      icon: <ZapIcon size={64} />,
      title: "Lightning Fast",
      description:
        "Experience blazing speeds with our cutting-edge technology.",
    },
    {
      icon: <LayersIcon size={64} />,
      title: "Scalable",
      description:
        "Grow your business without limits, our platform scales with you.",
    },
    {
      icon: <ShieldIcon size={64} />,
      title: "Secure",
      description: "Bank-level encryption keeps your data safe and protected.",
    },
  ];
  useEffect(() => {
    const updateActiveCardTop = () => {
      if (cardRefs.current[activeFeature]) {
        const rect = cardRefs.current[activeFeature].getBoundingClientRect();
        setActiveCardTop(
          rect.top - cardRefs.current[0].getBoundingClientRect().top,
        );
      }
    };
    updateActiveCardTop();
    window.addEventListener("resize", updateActiveCardTop);
    return () => {
      window.removeEventListener("resize", updateActiveCardTop);
    };
  }, [activeFeature]);
  return (
    <div
      className="w-full max-w-full bg-gray-900 text-white py-20 px-4 sm:px-6 lg:px-8" >
      <h2
        className="text-4xl font-extrabold text-center mb-16" >
        Futuristic Features
      </h2>
      <div className="relative">
        <div
          className="absolute inset-0 bg-gradient-to-r from-blue-500 to-purple-600 blur-3xl opacity-20" ></div>
        <div
          className="relative z-10 flex flex-col lg:flex-row items-start justify-center gap-12" >
          <motion.div
            className="w-full lg:w-1/2 space-y-8"
            initial={{
              opacity: 0,
              x: -50,
            }}
            animate={{
              opacity: 1,
              x: 0,
            }}
            transition={{
              duration: 0.5,
            }} >
            {features.map((feature, index) => (
              <motion.div
                key={index}
                ref={(el) => (cardRefs.current[index] = el)}
                className={`flex flex-col items-center p-4 rounded-lg cursor-pointer transition-colors ${activeFeature === index ? "bg-gray-800" : "hover:bg-gray-800"}`}
                onClick={() => setActiveFeature(index)}
                whileHover={{
                  scale: 1.05,
                }}
                whileTap={{
                  scale: 0.95,
                }} >
                <div className="mb-4 text-blue-400">
                  {feature.icon}
                </div>
                <div className="text-center">
                  <h3 className="text-2xl font-semibold">
                    {feature.title}
                  </h3>
                </div>
              </motion.div>
            ))}
          </motion.div>
          <motion.div
            className="w-full lg:w-1/2 p-8 bg-gradient-to-br from-gray-800 to-gray-900 rounded-2xl overflow-hidden flex flex-col justify-center space-y-6 shadow-lg"
            initial={{
              opacity: 0,
              x: 50,
            }}
            animate={{
              opacity: 1,
              x: 0,
              y: activeCardTop,
            }}
            transition={{
              duration: 0.5,
            }} >
            <motion.div
              className="text-xl font-medium text-center text-gray-200"
              key={activeFeature}
              initial={{
                opacity: 0,
                scale: 0.8,
              }}
              animate={{
                opacity: 1,
                scale: 1,
              }}
              transition={{
                duration: 0.3,
              }} >
              <div className="flex justify-center mb-4">
                {features[activeFeature].icon}
              </div>
              <h3 className="text-2xl font-bold mb-2">
                {features[activeFeature].title}
              </h3>
              <p className="text-gray-400">
                {features[activeFeature].description}
              </p>
            </motion.div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};
export default FeaturesSection;
render(
  <FeaturesSection />,
  document.getElementById("root"),
);
