import React, { useState, useEffect } from 'react';
import {
  Box,
  Flex,
  Text,
  Button,
  VStack,
  Container,
  Heading,
  useColorModeValue,
} from '@chakra-ui/react';
import { motion, useAnimation } from 'framer-motion';
import { FaRocket, FaBrain, FaCloud, FaLock } from 'react-icons/fa';

const MotionBox = motion(Box);
const MotionFlex = motion(Flex);
const MotionText = motion(Text);
const MotionHeading = motion(Heading);

const FloatingIcon = ({ icon: Icon, delay }) => (
  <MotionBox
    position="absolute"
    animate={{
      y: [0, -10, 0],
      opacity: [0.5, 1, 0.5],
      scale: [1, 1.2, 1],
    }}
    transition={{
      duration: 3,
      repeat: Infinity,
      delay,
    }}
  >
    <Icon size="2rem" color="blue.400" />
  </MotionBox>
);

const GlowingButton = ({ children, ...props }) => (
  <Button
    as={motion.button}
    whileHover={{ scale: 1.05 }}
    whileTap={{ scale: 0.95 }}
    position="relative"
    overflow="hidden"
    bg="blue.400"
    color="white"
    _hover={{ bg: 'blue.500' }}
    _before={{
      content: '""',
      position: 'absolute',
      top: '-50%',
      left: '-50%',
      width: '200%',
      height: '200%',
      backgroundColor: 'rgba(255,255,255,0.1)',
      transformOrigin: '0 100%',
      transform: 'rotate(45deg)',
      transition: 'all 0.3s',
    }}
    _hover={{
      _before: {
        transform: 'rotate(45deg) translateY(-100%)',
      },
    }}
    {...props}
  >
    {children}
  </Button>
);

export default function Index2() {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const controls = useAnimation();

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  useEffect(() => {
    controls.start({
      x: mousePosition.x / 50,
      y: mousePosition.y / 50,
    });
  }, [mousePosition, controls]);

  return (
    <Box bg="black" color="white" minH="100vh" overflow="hidden" position="relative">
      <Container maxW="container.xl" position="relative">
        <Flex as="nav" align="center" justify="space-between" wrap="wrap" padding="1.5rem" mb={8}>
          <MotionHeading
            as="h1"
            size="lg"
            letterSpacing={'tighter'}
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            NexusTech
          </MotionHeading>

          <MotionFlex
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            {['Products', 'Solutions', 'Pricing', 'Docs'].map((item, index) => (
              <MotionText
                key={item}
                cursor="pointer"
                mx={4}
                _hover={{ color: 'blue.400' }}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.95 }}
              >
                {item}
              </MotionText>
            ))}
          </MotionFlex>
        </Flex>

        <MotionFlex
          align="center"
          justify="space-between"
          direction={{ base: 'column', md: 'row' }}
          minH="70vh"
          px={8}
          mb={16}
        >
          <VStack spacing={6} align="flex-start" maxW="600px">
            <MotionHeading
              as="h2"
              size="3xl"
              fontWeight="bold"
              bgGradient="linear(to-r, blue.400, purple.500)"
              bgClip="text"
              textAlign="left"
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              Revolutionize Your Digital Landscape
            </MotionHeading>
            <MotionText
              fontSize="xl"
              color={useColorModeValue('gray.300', 'gray.300')}
              textAlign="left"
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
            >
              NexusTech empowers businesses with cutting-edge cloud solutions and AI-driven insights. Stay ahead in the digital race with our innovative platform.
            </MotionText>
            <MotionBox
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.4 }}
            >
              <GlowingButton
                size="lg"
                rightIcon={<FaRocket />}
              >
                Launch Your Project
              </GlowingButton>
            </MotionBox>
          </VStack>
          <Box w={{ base: '100%', md: '50%' }} mt={{ base: 12, md: 0 }} position="relative">
            <MotionBox
              animate={controls}
              initial={{ opacity: 0, scale: 0.8 }}
              whileInView={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.8 }}
            >
              <Box
                className="hero-graphic"
                h="400px"
                w="400px"
                bg="linear-gradient(45deg, #00A3FF, #0047FF)"
                borderRadius="full"
                boxShadow="0 0 50px #0047FF"
                position="relative"
                overflow="hidden"
              >
                <Box
                  className="hero-graphic-overlay"
                  position="absolute"
                  top="0"
                  left="0"
                  right="0"
                  bottom="0"
                  bg="url('data:image/svg+xml,%3Csvg width='20' height='20' viewBox='0 0 20 20' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='%239C92AC' fill-opacity='0.2' fill-rule='evenodd'%3E%3Ccircle cx='3' cy='3' r='3'/%3E%3Ccircle cx='13' cy='13' r='3'/%3E%3C/g%3E%3C/svg%3E')"
                />
              </Box>
            </MotionBox>
            <FloatingIcon icon={FaCloud} delay={0} />
            <FloatingIcon icon={FaBrain} delay={1} />
            <FloatingIcon icon={FaLock} delay={2} />
          </Box>
        </MotionFlex>
      </Container>

      <Box
        position="absolute"
        top="0"
        left="0"
        right="0"
        bottom="0"
        pointerEvents="none"
        background="radial-gradient(circle, transparent 10%, black 90%)"
        opacity="0.7"
      />

      <style>{`
        @keyframes pulse {
          0% { transform: scale(1); opacity: 0.5; }
          50% { transform: scale(1.05); opacity: 1; }
          100% { transform: scale(1); opacity: 0.5; }
        }

        .hero-graphic {
          animation: pulse 4s ease-in-out infinite;
        }

        .hero-graphic::before,
        .hero-graphic::after {
          content: '';
          position: absolute;
          top: -50%;
          left: -50%;
          width: 200%;
          height: 200%;
          background: conic-gradient(transparent, rgba(168, 239, 255, 0.1), transparent 30%);
          animation: rotate 6s linear infinite;
        }

        .hero-graphic::after {
          animation-duration: 10s;
        }

        @keyframes rotate {
          100% { transform: rotate(1turn); }
        }
      `}</style>
    </Box>
  );
}
