import React from 'react';
import { Card, CardBody, Button, Switch } from "@nextui-org/react";

const PricingTier = ({ name, price, features, isPopular }) => (
  <Card css={{ mw: "330px", p: "$6", m: "$5" }}>
    {isPopular && <p className="text-primary">Most Popular</p>}
    <h3>{name}</h3>
    <h2>${price}<span>/month</span></h2>
    <div className="my-4" />
    {features.map((feature, index) => (
      <p key={index}>â€¢ {feature}</p>
    ))}
    <div className="my-4" />
    <Button>Select Plan</Button>
  </Card>
);

const Pricing = () => {
  const [isAnnual, setIsAnnual] = React.useState(false);

  const plans = [
    {
      name: "Starter VPS",
      price: isAnnual ? 5 : 7,
      features: ["1 vCPU", "1 GB RAM", "25 GB SSD", "1 TB Transfer"],
    },
    {
      name: "Pro VPS",
      price: isAnnual ? 20 : 25,
      features: ["2 vCPU", "4 GB RAM", "80 GB SSD", "3 TB Transfer"],
      isPopular: true,
    },
    {
      name: "Business VPS",
      price: isAnnual ? 80 : 100,
      features: ["4 vCPU", "16 GB RAM", "320 GB SSD", "5 TB Transfer"],
    },
  ];

  return (
    <div className="container mx-auto">
      <h1 className="text-center text-4xl my-10">
        Choose Your VPS Plan
      </h1>
      <div className="flex justify-center items-center gap-2">
        <span>Monthly</span>
        <Switch 
          checked={isAnnual}
          onChange={(e) => setIsAnnual(e.target.checked)}
          size="lg"
        />
        <span>Annual (Save 20%)</span>
      </div>
      <div className="flex flex-wrap gap-4 justify-center">
        {plans.map((plan, index) => (
          <PricingTier key={index} {...plan} />
        ))}
      </div>
    </div>
  );
};

export default Pricing;
