import { SparklesIcon, ChevronRightIcon, CheckIcon } from "lucide-react";
import React, { useEffect, useRef } from "react";
import { render } from "react-dom";
const HeroSection = () => {
    const canvasRef = useRef(null);
    const floatingDivsRefs = useRef([]);
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas?.getContext("2d");
    if (!canvas || !ctx) return;
    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };
    const drawLines = () => {
      if (!ctx) return;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.strokeStyle = "rgba(255, 255, 255, 0.2)";
      ctx.lineWidth = 1;
      const center = {
        x: canvas.width / 2,
        y: canvas.height / 2,
      };
      floatingDivsRef.current.forEach((div) => {
        const rect = div.getBoundingClientRect();
        const start = {
          x: rect.left + rect.width / 2,
          y: rect.top + rect.height / 2,
        };
        ctx.beginPath();
        ctx.moveTo(start.x, start.y);
        ctx.lineTo(center.x, center.y);
        ctx.stroke();
        floatingDivsRef.current.forEach((otherDiv) => {
          if (div !== otherDiv) {
            const otherRect = otherDiv.getBoundingClientRect();
            const end = {
              x: otherRect.left + otherRect.width / 2,
              y: otherRect.top + otherRect.height / 2,
            };
            ctx.beginPath();
            ctx.moveTo(start.x, start.y);
            ctx.lineTo(end.x, end.y);
            ctx.stroke();
          }
        });
      });
    };
    resizeCanvas();
    drawLines();
    window.addEventListener("resize", () => {
      resizeCanvas();
      drawLines();
    });
    const observer = new MutationObserver(drawLines);
    observer.observe(document.body, {
      childList: true,
      subtree: true,
    });
    return () => {
      window.removeEventListener("resize", resizeCanvas);
      observer.disconnect();
    };
  }, []);
  return (
    <div
      className="bg-black text-white min-h-screen relative overflow-hidden" >
      <canvas
        ref={canvasRef}
        className="absolute top-0 left-0 w-full h-full pointer-events-none" />
      <header
        className="p-4 flex justify-between items-center" >
        <SparklesIcon className="h-6 w-6" />
        <nav>
          <ul className="flex space-x-6">
            <li>Products</li>
            <li>Resources</li>
            <li>Customer</li>
            <li>Company</li>
          </ul>
        </nav>
        <button
          className="bg-gray-800 text-white px-4 py-2 rounded-full flex items-center" >
          Get Started
          <ChevronRightIcon className="ml-2 h-4 w-4" />
        </button>
      </header>

      <main
        className="flex flex-col items-center justify-center text-center px-4 mt-20" >
        <p className="text-sm mb-4">
          « SOFTWARE AI PLATFORM »
        </p>
        <h1
          className="text-5xl md:text-7xl font-serif mb-4 bg-gradient-to-r from-purple-400 to-pink-300 text-transparent bg-clip-text" >
          The Modern
          <br />
          Software Platform
        </h1>
        <p className="mb-8 max-w-2xl">
          An easy-to-understand summary of your that concise picture of your
          financial situation. Foster Collaboration and Productivity with Pair
          of Programming
        </p>

        <div
          className="flex flex-wrap justify-center gap-4 mb-8" >
          <div className="bg-gray-800 p-4 rounded-lg">
            <h2 className="text-xl mb-2">
              Request to access
            </h2>
            <p className="text-sm mb-4">
              Elevate your designs & websites
            </p>
            <button
              className="bg-purple-400 text-black px-6 py-2 rounded-full" >
              Request Access
            </button>
          </div>
          <div className="bg-gray-800 p-4 rounded-lg">
            <h2 className="text-xl mb-2">
              See our simple pricing
            </h2>
            <p className="text-sm mb-4">
              Elevate your designs & websites to us
            </p>
            <button
              className="bg-gray-700 text-white px-6 py-2 rounded-full" >
              Our Pricing
            </button>
          </div>
        </div>
      </main>

      <div
        className="absolute top-40 left-10" ref={(el) => el && floatingDivsRef.current.push(el)}
      >
        <div
          className="bg-gray-800 px-4 py-2 rounded-full flex items-center" >
          <CheckIcon className="mr-2 h-4 w-4" />
          <span>Easy-to-understand tools</span>
        </div>
      </div>

      <div
        className="absolute bottom-40 left-20" ref={(el) => el && floatingDivsRef.current.push(el)}
      >
        <div
          className="bg-gray-800 px-4 py-2 rounded-full flex items-center" >
          <CheckIcon className="mr-2 h-4 w-4" />
          <span>File Sharing: AI Feature</span>
        </div>
      </div>

      <div
        className="absolute top-60 right-10" ref={(el) => el && floatingDivsRef.current.push(el)}
      >
        <div
          className="bg-gray-800 px-4 py-2 rounded-full flex items-center" >
          <CheckIcon className="mr-2 h-4 w-4" />
          <span>All tasks within your teams</span>
        </div>
      </div>

      <div
        className="absolute bottom-20 left-1/2 transform -translate-x-1/2" ref={(el) => el && floatingDivsRef.current.push(el)}
      >
        <div
          className="bg-gray-800 px-4 py-2 rounded-full flex items-center" >
          <CheckIcon className="mr-2 h-4 w-4" />
          <span>Task Management and Team Management</span>
        </div>
      </div>

      <div
        className="absolute bottom-60 right-20" ref={(el) => el && floatingDivsRef.current.push(el)}
      >
        <div
          className="bg-gray-800 px-4 py-2 rounded-full flex items-center" >
          <CheckIcon className="mr-2 h-4 w-4" />
          <span>10M+ Brand supported</span>
        </div>
      </div>
    </div>
  );
};
export default HeroSection;
render(<HeroSection />, document.getElementById("root"));
