import { ZapIcon, LayersIcon, ShieldIcon, GlobeIcon, HeartIcon, BoltIcon, ServerIcon, LockIcon, UsersIcon, SmileIcon, CloudIcon, DatabaseIcon, RefreshCwIcon, SettingsIcon, PhoneIcon, MessageSquareIcon, BellIcon, ClockIcon, BarChartIcon, TrendingUpIcon } from "lucide-react";
import React, { useEffect, useState, useRef } from "react";
import { render } from "react-dom";
import { motion } from "framer-motion";

const FeaturesSection = () => {
  const [activeFeature, setActiveFeature] = useState(0);
  const [activeCardTop, setActiveCardTop] = useState(0);
  const cardRefs = useRef([]);
  const features = [
    {
      icon: <ZapIcon size={64} />,
      title: "Lightning Fast",
      miniFeatures: [
        { icon: <BoltIcon size={32} />, description: "Quick Load" },
        { icon: <ServerIcon size={32} />, description: "Optimized Backend" },
        { icon: <CloudIcon size={32} />, description: "Cloud Acceleration" },
        { icon: <DatabaseIcon size={32} />, description: "Efficient Caching" },
        { icon: <RefreshCwIcon size={32} />, description: "Real-time Updates" },
      ],
    },
    {
      icon: <LayersIcon size={64} />,
      title: "Scalable",
      miniFeatures: [
        { icon: <ServerIcon size={32} />, description: "Elastic Infrastructure" },
        { icon: <UsersIcon size={32} />, description: "Multi-tenant Support" },
        { icon: <SettingsIcon size={32} />, description: "Easy Configuration" },
        { icon: <TrendingUpIcon size={32} />, description: "Growth Ready" },
        { icon: <DatabaseIcon size={32} />, description: "Dynamic Resources" },
      ],
    },
    {
      icon: <ShieldIcon size={64} />,
      title: "Secure",
      miniFeatures: [
        { icon: <LockIcon size={32} />, description: "End-to-end Encryption" },
        { icon: <ShieldIcon size={32} />, description: "Advanced Firewall" },
        { icon: <UsersIcon size={32} />, description: "User Authentication" },
        { icon: <RefreshCwIcon size={32} />, description: "Regular Updates" },
        { icon: <BellIcon size={32} />, description: "Threat Alerts" },
      ],
    },
    {
      icon: <GlobeIcon size={64} />,
      title: "Global Reach",
      miniFeatures: [
        { icon: <GlobeIcon size={32} />, description: "CDN Integration" },
        { icon: <UsersIcon size={32} />, description: "Multi-language Support" },
        { icon: <ClockIcon size={32} />, description: "24/7 Availability" },
        { icon: <PhoneIcon size={32} />, description: "Global Support" },
        { icon: <MessageSquareIcon size={32} />, description: "Local Chatbots" },
      ],
    },
    {
      icon: <HeartIcon size={64} />,
      title: "User-Friendly",
      miniFeatures: [
        { icon: <SmileIcon size={32} />, description: "Intuitive UI" },
        { icon: <HeartIcon size={32} />, description: "Accessibility Features" },
        { icon: <SettingsIcon size={32} />, description: "Easy Customization" },
        { icon: <MessageSquareIcon size={32} />, description: "Helpful Tooltips" },
        { icon: <BarChartIcon size={32} />, description: "Visual Analytics" },
      ],
    },
  ];

  useEffect(() => {
    const updateActiveCardTop = () => {
      if (cardRefs.current[activeFeature]) {
        const rect = cardRefs.current[activeFeature].getBoundingClientRect();
        setActiveCardTop(
          rect.top - cardRefs.current[0].getBoundingClientRect().top,
        );
      }
    };
    updateActiveCardTop();
    window.addEventListener("resize", updateActiveCardTop);
    return () => {
      window.removeEventListener("resize", updateActiveCardTop);
    };
  }, [activeFeature]);

  return (
    <div className="w-screen min-h-screen bg-black text-white py-20 px-4 sm:px-6 lg:px-8">
      <h2 className="text-4xl font-extrabold text-center mb-16">
        Futuristic Features
      </h2>
      <div className="relative">
        <div className="relative z-10 flex flex-col lg:flex-row items-start justify-center gap-12">
          <motion.div
            className="w-full lg:w-1/2 space-y-8"
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
          >
            {features.map((feature, index) => (
              <motion.div
                key={index}
                ref={(el) => (cardRefs.current[index] = el)}
                className={`flex flex-col items-center p-4 rounded-lg cursor-pointer transition-colors border-2 ${
                  activeFeature === index
                    ? "bg-gray-900 border-green-500"
                    : "bg-black border-gray-700 hover:bg-gray-900 hover:border-green-500"
                }`}
                onClick={() => setActiveFeature(index)}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <div className="mb-4 text-green-500">
                  {feature.icon}
                </div>
                <div className="text-center">
                  <h3 className="text-2xl font-semibold">
                    {feature.title}
                  </h3>
                </div>
              </motion.div>
            ))}
          </motion.div>
          <motion.div
            className="w-full lg:w-1/2 p-8 bg-gradient-to-br from-gray-900 to-black rounded-2xl overflow-hidden flex flex-col justify-center space-y-6 shadow-lg"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0, y: activeCardTop }}
            transition={{ duration: 0.5 }}
          >
            <motion.div
              className="text-xl font-medium text-center text-gray-200"
              key={activeFeature}
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.3 }}
            >
              <div className="flex justify-center mb-4 text-green-500">
                {features[activeFeature].icon}
              </div>
              <h3 className="text-2xl font-bold mb-6">
                {features[activeFeature].title}
              </h3>
              <div className="grid grid-cols-2 gap-6">
                {features[activeFeature].miniFeatures.map((miniFeature, index) => (
                  <div key={index} className="flex flex-col items-center">
                    <div className="text-green-500 mb-2">{miniFeature.icon}</div>
                    <p className="text-sm text-gray-400">{miniFeature.description}</p>
                  </div>
                ))}
              </div>
            </motion.div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default FeaturesSection;

render(
  <FeaturesSection />,
  document.getElementById("root"),
);
