import React from 'react';
import { motion } from 'framer-motion';

const FeatureCard = ({ title, image }) => (
  <div className="flex-shrink-0 w-[400px] h-[300px] bg-gray-800 rounded-lg overflow-hidden shadow-lg mr-8 relative">
    <img src={image} alt={title} className="w-full h-full object-cover" />
    <div className="absolute bottom-4 left-4 bg-black bg-opacity-70 px-4 py-2 rounded-lg">
      <h3 className="text-sm font-semibold text-white">{title}</h3>
    </div>
  </div>
);

function Info1() {
  const cards = [
    {
      title: "Data sources",
      image: "https://via.placeholder.com/400x300?text=Data+Sources"
    },
    {
      title: "Components",
      image: "https://via.placeholder.com/400x300?text=Components"
    },
    {
      title: "Workflows",
      image: "https://via.placeholder.com/400x300?text=Workflows"
    },
    {
      title: "AI Insights",
      image: "https://via.placeholder.com/400x300?text=AI+Insights"
    }
  ];

  return (
    <div className="min-h-screen bg-black text-white overflow-hidden relative py-20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div 
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1 }}
          className="text-center mb-40 relative"
        >
          <motion.div
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 0.1 }}
            transition={{ duration: 1, delay: 0.5 }}
            className="absolute inset-0 bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 rounded-full filter blur-3xl"
          />
          <motion.p
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            className="text-lg text-blue-400 mb-8 relative z-10"
          >
            AI-powered no code apps
          </motion.p>
          <motion.h2 
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            className="text-6xl font-bold mb-16 relative z-10"
          >
            Build business software
          </motion.h2>
          <motion.h3
            initial={{ scale: 0, opacity: 0, rotateX: -90 }}
            animate={{ scale: 1, opacity: 1, rotateX: 0 }}
            transition={{ 
              duration: 1.2, 
              delay: 1,
              type: "spring",
              stiffness: 100,
              damping: 10
            }}
            className="text-5xl font-bold mb-20 relative z-10"
          >
            <motion.span
              animate={{
                color: ['#4ade80', '#22d3ee', '#4ade80'],
                textShadow: ['0 0 5px #4ade80', '0 0 20px #22d3ee', '0 0 5px #4ade80'],
              }}
              transition={{
                duration: 3,
                repeat: Infinity,
                repeatType: 'reverse',
              }}
              className="inline-block"
            >
              you actually want
            </motion.span>
            <motion.div
              initial={{ scale: 0, opacity: 0 }}
              animate={{ scale: [1, 1.2, 1], opacity: [0, 1, 0] }}
              transition={{
                duration: 1.5,
                delay: 1.2,
                times: [0, 0.5, 1],
                repeat: 0,
              }}
              className="absolute inset-0 bg-gradient-to-r from-yellow-400 via-yellow-300 to-yellow-500 rounded-full filter blur-md z-[-1]"
            />
          </motion.h3>
        </motion.div>
      </div>

      <div className="relative mt-40">
        <div className="absolute inset-y-0 left-0 w-1/12 bg-gradient-to-r from-black to-transparent z-10"></div>
        <div className="absolute inset-y-0 right-0 w-1/12 bg-gradient-to-l from-black to-transparent z-10"></div>
        <motion.div 
          className="flex"
          animate={{ x: [0, -1600] }}
          transition={{ 
            repeat: Infinity, 
            duration: 40, 
            ease: "linear"
          }}
        >
          {[...cards, ...cards].map((card, index) => (
            <FeatureCard key={index} {...card} />
          ))}
        </motion.div>
      </div>
    </div>
  );
}

export default Info1;
