import React, { useRef, useEffect } from 'react';
import { motion, useAnimation } from 'framer-motion';

const FeatureCard = ({ title, description, image }) => (
  <div className="flex-shrink-0 w-96 h-64 bg-gray-800 rounded-lg overflow-hidden shadow-lg mr-6">
    <img src={image} alt={title} className="w-full h-40 object-cover" />
    <div className="p-4">
      <h3 className="text-lg font-semibold text-white">{title}</h3>
      <p className="text-sm text-gray-400">{description}</p>
    </div>
  </div>
);

function Info1() {
  const cardsRef = useRef(null);
  const controls = useAnimation();

  useEffect(() => {
    const scrollCards = async () => {
      await controls.start({ x: '-100%', transition: { duration: 20, ease: 'linear' } });
      controls.set({ x: '0%' });
      scrollCards();
    };
    scrollCards();
  }, [controls]);

  return (
    <div className="min-h-screen bg-black text-white overflow-hidden relative">
      <div className="absolute top-10 left-10">
        <h2 className="text-5xl font-bold mb-2">
          Build business software <br /> you actually want
        </h2>
        <p className="text-xl text-blue-400">
          AI-powered no code apps
        </p>
      </div>

      <div className="absolute top-1/4 right-10 max-w-md">
        <p className="text-lg text-gray-400">
          Glide makes it easy to build and deploy custom tools your business needs and team will love â€” with clicks, not code.
        </p>
        <button className="mt-6 px-8 py-3 bg-blue-500 text-white font-semibold rounded-full hover:bg-blue-600 transition duration-300">
          Start for free
        </button>
      </div>

      <motion.div 
        ref={cardsRef}
        className="absolute bottom-20 flex"
        animate={controls}
      >
        <FeatureCard
          title="Data sources that sync and scale"
          description="Connect to your existing data or start from scratch with spreadsheet-like tables."
          image="/path/to/data-table-image.jpg"
        />
        <FeatureCard
          title="Drag-and-drop components"
          description="Build your interface with pre-built components for tables, charts, forms, and more."
          image="/path/to/chart-image.jpg"
        />
        <FeatureCard
          title="Build custom workflows"
          description="Create powerful automations and integrations to streamline your processes."
          image="/path/to/workflow-image.jpg"
        />
      </motion.div>
    </div>
  );
}

export default Info1;
