import React from 'react';
import { motion } from 'framer-motion';

const FeatureCard = ({ title, description, image }) => (
  <div className="flex-shrink-0 w-[400px] h-[300px] bg-gray-800 rounded-lg overflow-hidden shadow-lg mr-8">
    <img src={image} alt={title} className="w-full h-48 object-cover" />
    <div className="p-6">
      <h3 className="text-xl font-semibold text-white mb-2">{title}</h3>
      <p className="text-sm text-gray-400">{description}</p>
    </div>
  </div>
);

function Info1() {
  const cards = [
    {
      title: "Data sources that sync and scale",
      description: "Connect to your existing data or start from scratch with spreadsheet-like tables.",
      image: "https://via.placeholder.com/400x200?text=Data+Sources"
    },
    {
      title: "Drag-and-drop components",
      description: "Build your interface with pre-built components for tables, charts, forms, and more.",
      image: "https://via.placeholder.com/400x200?text=Components"
    },
    {
      title: "Build custom workflows",
      description: "Create powerful automations and integrations to streamline your processes.",
      image: "https://via.placeholder.com/400x200?text=Workflows"
    },
    {
      title: "AI-powered insights",
      description: "Leverage machine learning to gain valuable insights from your data.",
      image: "https://via.placeholder.com/400x200?text=AI+Insights"
    }
  ];

  return (
    <div className="min-h-screen bg-black text-white overflow-hidden relative py-20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-20">
          <h2 className="text-6xl font-bold mb-4">
            Build business software <br /> you actually want
          </h2>
          <p className="text-2xl text-blue-400 mb-8">
            AI-powered no code apps
          </p>
          <p className="text-xl text-gray-400 max-w-3xl mx-auto">
            Glide makes it easy to build and deploy custom tools your business needs and team will love â€” with clicks, not code.
          </p>
          <button className="mt-8 px-8 py-4 bg-blue-500 text-white text-lg font-semibold rounded-full hover:bg-blue-600 transition duration-300">
            Start for free
          </button>
        </div>
      </div>

      <div className="relative">
        <div className="absolute inset-y-0 left-0 w-1/12 bg-gradient-to-r from-black to-transparent z-10"></div>
        <div className="absolute inset-y-0 right-0 w-1/12 bg-gradient-to-l from-black to-transparent z-10"></div>
        <motion.div 
          className="flex"
          animate={{ x: [0, -1600] }}
          transition={{ 
            repeat: Infinity, 
            duration: 40, 
            ease: "linear"
          }}
        >
          {[...cards, ...cards].map((card, index) => (
            <FeatureCard key={index} {...card} />
          ))}
        </motion.div>
      </div>
    </div>
  );
}

export default Info1;
