import { ZapIcon, LayersIcon, ShieldIcon, GlobeIcon, HeartIcon, VideoIcon, AppleIcon } from "lucide-react";
import React, { useEffect, useState, useRef } from "react";
import { motion, useAnimation, useInView, useScroll, useTransform } from "framer-motion";

const FeaturesSection = () => {
  const [activeFeature, setActiveFeature] = useState(0);
  const cardRefs = useRef([]);
  const sectionRef = useRef(null);
  const { scrollYProgress } = useScroll();
  const blueToGreen = useTransform(scrollYProgress, [0, 1], ["#3B82F6", "#10B981"]);

  const features = [
    {
      icon: <ZapIcon size={64} />,
      title: "Lightning Fast",
      description: "Experience blazing speeds with our cutting-edge technology.",
    },
    {
      icon: <LayersIcon size={64} />,
      title: "Scalable",
      description: "Grow your business without limits, our platform scales with you.",
    },
    {
      icon: <ShieldIcon size={64} />,
      title: "Secure",
      description: "Bank-level encryption keeps your data safe and protected.",
    },
    {
      icon: <GlobeIcon size={64} />,
      title: "Global Reach",
      description: "Connect with users worldwide, expanding your market presence.",
    },
    {
      icon: <HeartIcon size={64} />,
      title: "User-Friendly",
      description: "Intuitive design ensures a smooth experience for all users.",
    },
  ];

  useEffect(() => {
    const updateActiveFeature = () => {
      const sectionTop = sectionRef.current.offsetTop;
      const scrollPosition = window.scrollY + window.innerHeight / 2;

      let newActiveFeature = 0;
      cardRefs.current.forEach((card, index) => {
        if (card && scrollPosition >= sectionTop + card.offsetTop) {
          newActiveFeature = index;
        }
      });

      setActiveFeature(newActiveFeature);
    };

    updateActiveFeature();
    window.addEventListener("scroll", updateActiveFeature);

    return () => {
      window.removeEventListener("scroll", updateActiveFeature);
    };
  }, []);

  return (
    <div ref={sectionRef} className="w-screen min-h-screen bg-gradient-to-b from-gray-900 to-black text-white py-20 px-4 sm:px-6 lg:px-8 overflow-hidden relative">
      <div className="absolute inset-0 z-0">
        <motion.div
          style={{ background: useTransform(blueToGreen, (color) => `linear-gradient(to bottom, ${color}, transparent)`) }}
          className="absolute top-0 left-1/4 w-0.5 h-full opacity-20"
        ></motion.div>
        <motion.div
          style={{ background: useTransform(blueToGreen, (color) => `linear-gradient(to bottom, ${color}, transparent)`) }}
          className="absolute top-0 right-1/4 w-0.5 h-full opacity-20"
        ></motion.div>
        <motion.div
          style={{ background: useTransform(blueToGreen, (color) => `linear-gradient(to right, transparent, ${color}, transparent)`) }}
          className="absolute top-1/4 left-0 w-full h-0.5 opacity-20"
        ></motion.div>
        <motion.div
          style={{ background: useTransform(blueToGreen, (color) => `linear-gradient(to right, transparent, ${color}, transparent)`) }}
          className="absolute bottom-1/4 left-0 w-full h-0.5 opacity-20"
        ></motion.div>
      </div>

      <h2 className="text-4xl font-extrabold text-center mb-16 relative z-10">
        Futuristic Features
      </h2>
      <div className="relative z-10 flex flex-col lg:flex-row items-start justify-center gap-12">
        <motion.div className="w-full lg:w-1/2 space-y-8">
          {features.map((feature, index) => (
            <motion.div
              key={index}
              ref={(el) => (cardRefs.current[index] = el)}
              className={`flex flex-col items-center p-4 rounded-lg cursor-pointer transition-colors border-2 ${
                activeFeature === index
                  ? "bg-gray-800 border-blue-400"
                  : "bg-gray-900 border-gray-700 hover:bg-gray-800 hover:border-blue-400"
              }`}
              onClick={() => {
                setActiveFeature(index);
                cardRefs.current[index].scrollIntoView({ behavior: 'smooth', block: 'center' });
              }}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <motion.div className="mb-4 text-blue-400" style={{ color: blueToGreen }}>{feature.icon}</motion.div>
              <div className="text-center">
                <h3 className="text-2xl font-semibold">{feature.title}</h3>
              </div>
            </motion.div>
          ))}
        </motion.div>
        <motion.div
          className="w-full lg:w-1/2 p-8 bg-gradient-to-br from-gray-800 to-gray-900 rounded-2xl overflow-hidden flex flex-col justify-center space-y-6 shadow-lg sticky top-24"
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5 }}
        >
          <motion.div
            className="text-xl font-medium text-center text-gray-200"
            key={activeFeature}
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.3 }}
          >
            <motion.div className="flex justify-center mb-4" style={{ color: blueToGreen }}>{features[activeFeature].icon}</motion.div>
            <h3 className="text-2xl font-bold mb-2">{features[activeFeature].title}</h3>
            <p className="text-gray-400">{features[activeFeature].description}</p>
          </motion.div>
        </motion.div>
      </div>

      <motion.div 
        className="absolute bottom-8 right-8 z-10"
        initial={{ opacity: 0, x: 20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.8, delay: 0.7 }}
      >
        <VideoIcon className="text-gray-600" size={36} />
      </motion.div>
    </div>
  );
};

export default FeaturesSection;
