import React from 'react';
import { Container, Grid, Card, Text, Button, Spacer, Switch } from "@nextui-org/react";

const PricingTier = ({ name, price, features, isPopular }) => (
  <Card css={{ mw: "330px", p: "$6", m: "$5" }}>
    {isPopular && <Text color="primary">Most Popular</Text>}
    <Text h3>{name}</Text>
    <Text h2>${price}<Text span>/month</Text></Text>
    <Spacer y={1} />
    {features.map((feature, index) => (
      <Text key={index}>â€¢ {feature}</Text>
    ))}
    <Spacer y={1} />
    <Button>Select Plan</Button>
  </Card>
);

const Pricing = () => {
  const [isAnnual, setIsAnnual] = React.useState(false);

  const plans = [
    {
      name: "Starter VPS",
      price: isAnnual ? 5 : 7,
      features: ["1 vCPU", "1 GB RAM", "25 GB SSD", "1 TB Transfer"],
    },
    {
      name: "Pro VPS",
      price: isAnnual ? 20 : 25,
      features: ["2 vCPU", "4 GB RAM", "80 GB SSD", "3 TB Transfer"],
      isPopular: true,
    },
    {
      name: "Business VPS",
      price: isAnnual ? 80 : 100,
      features: ["4 vCPU", "16 GB RAM", "320 GB SSD", "5 TB Transfer"],
    },
  ];

  return (
    <Container>
      <Text h1 css={{ textAlign: "center", my: "$10" }}>
        Choose Your VPS Plan
      </Text>
      <Grid.Container justify="center" alignItems="center">
        <Text>Monthly</Text>
        <Switch 
          checked={isAnnual}
          onChange={(e) => setIsAnnual(e.target.checked)}
          size="lg"
        />
        <Text>Annual (Save 20%)</Text>
      </Grid.Container>
      <Grid.Container gap={2} justify="center">
        {plans.map((plan, index) => (
          <Grid key={index}>
            <PricingTier {...plan} />
          </Grid>
        ))}
      </Grid.Container>
    </Container>
  );
};

export default Pricing;
