import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FaRocket, FaLightbulb, FaCode, FaChartLine, FaChevronDown, FaCog, FaBook, FaHeadset, FaVideo, FaNewspaper, FaGraduationCap } from 'react-icons/fa';

function DropdownMenu({ title, items }) {
  const [isOpen, setIsOpen] = useState(false);

  const getIcon = (iconName) => {
    switch (iconName) {
      case 'FaCog': return <FaCog />;
      case 'FaBook': return <FaBook />;
      case 'FaHeadset': return <FaHeadset />;
      case 'FaVideo': return <FaVideo />;
      case 'FaNewspaper': return <FaNewspaper />;
      case 'FaGraduationCap': return <FaGraduationCap />;
      default: return <FaCog />;
    }
  };

  return (
    <div className="relative" onMouseEnter={() => setIsOpen(true)} onMouseLeave={() => setIsOpen(false)}>
      <button className="flex items-center hover:text-indigo-400 transition-colors">
        {title}
        <FaChevronDown className="ml-1 h-3 w-3" />
      </button>
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            transition={{ duration: 0.2 }}
            className={`absolute right-0 mt-2 w-96 rounded-md shadow-lg bg-gray-800 ring-1 ring-black ring-opacity-5 p-4 ${title === 'Resources' ? 'h-auto' : ''}`}
          >
            <div className={`grid grid-cols-2 gap-3 ${title === 'Resources' ? 'grid-rows-3' : ''}`} role="menu" aria-orientation="vertical" aria-labelledby="options-menu">
              {items.map((item, index) => (
                <a
                  key={index}
                  href="#"
                  className="flex flex-col items-center p-2 rounded-md hover:bg-gray-700 transition-colors border border-white border-opacity-10 hover:border-opacity-20"
                  role="menuitem"
                >
                  <div className="text-indigo-400 mb-1">
                    {getIcon(item.icon)}
                  </div>
                  <div className="text-xs font-medium text-white">{item.title}</div>
                  <div className="text-xs text-gray-400 mt-1 text-center">{item.description}</div>
                </a>
              ))}
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

function MessageBubble({ text, sender, delay, isVisible }) {
  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: isVisible ? 1 : 0, y: isVisible ? 0 : 20 }}
      transition={{ duration: 0.5, delay: isVisible ? delay : 0 }}
      className={`max-w-xs ${sender === 'user' ? 'ml-auto bg-indigo-600' : 'mr-auto bg-gray-700'} rounded-lg p-3 mb-2`}
    >
      <p className="text-sm">{text}</p>
    </motion.div>
  );
}

function MessagingVisual() {
  const [currentMessage, setCurrentMessage] = useState(0);
  const [isComplete, setIsComplete] = useState(false);
  const messages = [
    { text: "Hey, I heard about TokTok. What's it all about?", sender: 'user' },
    { text: "TokTok is an innovative platform for software development. It offers cutting-edge solutions for visionaries.", sender: 'bot' },
    { text: "Sounds interesting! What makes it special?", sender: 'user' },
    { text: "It pushes the boundaries of what's possible in tech. Think AI, blockchain, and more, all in one place.", sender: 'bot' },
    { text: "Wow! How can I get started?", sender: 'user' },
  ];

  useEffect(() => {
    if (currentMessage < messages.length - 1) {
      const timer = setTimeout(() => {
        setCurrentMessage(prev => prev + 1);
      }, 3000);
      return () => clearTimeout(timer);
    } else {
      setIsComplete(true);
    }
  }, [currentMessage]);

  return (
    <div className="w-full max-w-md bg-gray-800 rounded-xl p-4 shadow-lg" style={{ height: '430px' }}>
      <div className="flex items-center mb-4">
        <div className="w-3 h-3 bg-red-500 rounded-full mr-2"></div>
        <div className="w-3 h-3 bg-yellow-500 rounded-full mr-2"></div>
        <div className="w-3 h-3 bg-green-500 rounded-full"></div>
      </div>
      <div className="space-y-2 h-[calc(100%-2rem)] overflow-y-auto">
        {messages.map((message, index) => (
          <MessageBubble 
            key={index} 
            text={message.text} 
            sender={message.sender} 
            delay={isComplete ? 0 : index * 0.5}
            isVisible={index <= currentMessage}
          />
        ))}
      </div>
    </div>
  );
}

function Index() {
  const productItems = [
    { icon: 'FaCog', title: 'Product A', description: 'Innovative solution' },
    { icon: 'FaBook', title: 'Product B', description: 'Streamline workflow' },
    { icon: 'FaHeadset', title: 'Product C', description: 'Enhance productivity' },
    { icon: 'FaCog', title: 'Product D', description: 'Next-gen tech' },
  ];

  const serviceItems = [
    { icon: 'FaCog', title: 'Service X', description: 'Custom solutions' },
    { icon: 'FaBook', title: 'Service Y', description: 'Expert consulting' },
    { icon: 'FaHeadset', title: 'Service Z', description: '24/7 assistance' },
    { icon: 'FaCog', title: 'Service W', description: 'Implementation' },
  ];

  const resourceItems = [
    { icon: 'FaBook', title: 'Blog', description: 'Latest insights' },
    { icon: 'FaCog', title: 'Docs', description: 'Guides & tutorials' },
    { icon: 'FaHeadset', title: 'Support', description: 'Expert help' },
    { icon: 'FaVideo', title: 'Webinars', description: 'Learn from experts' },
    { icon: 'FaNewspaper', title: 'News', description: 'Stay updated' },
    { icon: 'FaGraduationCap', title: 'Courses', description: 'In-depth learning' },
  ];

  return (
    <div className="h-screen bg-gradient-to-br from-gray-900 to-gray-800 text-white overflow-hidden flex flex-col justify-between py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 w-full flex flex-col h-full">
        {/* Top section with logo, navbar, and icons */}
        <motion.div 
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="flex justify-between items-center relative"
        >
          <div className="text-2xl font-bold">TokTok</div>

          {/* Centered floating navbar */}
          <nav className="absolute left-1/2 transform -translate-x-1/2">
            <ul className="flex items-center space-x-6 bg-gray-800 bg-opacity-50 backdrop-filter backdrop-blur-lg rounded-full px-6 py-2">
              <li><a href="#" className="hover:text-indigo-400 transition-colors">Home</a></li>
              <li><a href="#" className="hover:text-indigo-400 transition-colors">Pricing</a></li>
              <li><a href="#" className="hover:text-indigo-400 transition-colors">Contact</a></li>
              <li><a href="#" className="hover:text-indigo-400 transition-colors">About</a></li>
              <li className="flex space-x-4">
                <DropdownMenu title="Products" items={productItems} />
                <DropdownMenu title="Services" items={serviceItems} />
                <DropdownMenu title="Resources" items={resourceItems} />
              </li>
            </ul>
          </nav>

          <div className="flex space-x-6">
            <FaRocket className="h-6 w-6 text-indigo-400" />
            <FaLightbulb className="h-6 w-6 text-indigo-400" />
            <FaCode className="h-6 w-6 text-indigo-400" />
            <FaChartLine className="h-6 w-6 text-indigo-400" />
          </div>
        </motion.div>

        {/* Main hero content */}
        <div className="flex-grow flex items-center">
          <div className="flex w-full items-center">
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
              className="w-1/2 text-left pr-8 flex flex-col justify-center"
            >
              <h1 className="text-4xl tracking-tight font-extrabold sm:text-5xl md:text-6xl">
                <span className="block">Revolutionize Your</span>
                <span className="block text-indigo-400 whitespace-nowrap">Software Development</span>
              </h1>
              <p className="mt-3 text-base text-gray-300 sm:text-lg md:mt-5 md:text-xl max-w-lg">
                TokTok delivers cutting-edge solutions for visionaries. 
                Join our exclusive platform and push the boundaries of what's possible.
              </p>
              <div className="mt-10 flex">
                <motion.div
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  className="rounded-md shadow"
                >
                  <a href="#" className="w-full flex items-center justify-center px-8 py-3 border border-transparent text-base font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 md:py-4 md:text-lg md:px-10">
                    Get Started
                  </a>
                </motion.div>
                <motion.div
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  className="ml-3 rounded-md shadow"
                >
                  <a href="#" className="w-full flex items-center justify-center px-8 py-3 border border-transparent text-base font-medium rounded-md text-indigo-600 bg-white hover:bg-gray-50 md:py-4 md:text-lg md:px-10">
                    Learn More
                  </a>
                </motion.div>
              </div>
            </motion.div>
            <motion.div
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.4 }}
              className="w-1/2 flex justify-center items-center"
            >
              <MessagingVisual />
            </motion.div>
          </div>
        </div>

        {/* Bottom section with "Trusted by" */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.6 }}
        >
          <div className="relative">
            <div className="absolute inset-0 flex items-center" aria-hidden="true">
              <div className="w-full border-t border-gray-700" />
            </div>
            <div className="relative flex justify-center">
              <span className="px-2 bg-gray-900 text-sm text-gray-400">
                Trusted by industry leaders
              </span>
            </div>
          </div>

          <div className="mt-8 grid grid-cols-2 gap-8 md:grid-cols-6 lg:grid-cols-5">
            {['Company 1', 'Company 2', 'Company 3', 'Company 4', 'Company 5'].map((company, index) => (
              <div key={index} className="col-span-1 flex justify-center md:col-span-2 lg:col-span-1">
                <div className="text-gray-400 font-semibold">{company}</div>
              </div>
            ))}
          </div>
        </motion.div>
      </div>
    </div>
  );
}

export default Index;
