import React, { useState } from 'react';
import {
  Box,
  Flex,
  Text,
  Button,
  VStack,
  HStack,
  Container,
  Heading,
  useColorModeValue,
  IconButton,
  Collapse,
} from '@chakra-ui/react';
import { motion, AnimatePresence } from 'framer-motion';
import { FaRocket, FaBrain, FaCloud, FaLock } from 'react-icons/fa';
import { HamburgerIcon, CloseIcon } from '@chakra-ui/icons';

const MotionBox = motion(Box);
const MotionFlex = motion(Flex);
const MotionText = motion(Text);

const FeatureCard = ({ icon, title, description }) => (
  <MotionBox
    initial={{ opacity: 0, y: 20 }}
    animate={{ opacity: 1, y: 0 }}
    transition={{ duration: 0.5 }}
    bg="rgba(255,255,255,0.05)"
    p={6}
    borderRadius="lg"
    boxShadow="xl"
    backdropFilter="blur(10px)"
    border="1px solid rgba(255,255,255,0.1)"
  >
    <Flex align="center" mb={4}>
      <Box as={icon} size="24px" color="blue.400" mr={3} />
      <Heading size="md">{title}</Heading>
    </Flex>
    <Text fontSize="sm">{description}</Text>
  </MotionBox>
);

export default function Index2() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <Box bg="black" color="white" minH="100vh" overflow="hidden">
      <Container maxW="container.xl" position="relative">
        <Flex as="nav" align="center" justify="space-between" wrap="wrap" padding="1.5rem" mb={8}>
          <Flex align="center" mr={5}>
            <Heading as="h1" size="lg" letterSpacing={'tighter'}>
              NexusTech
            </Heading>
          </Flex>

          <IconButton
            size="md"
            icon={isOpen ? <CloseIcon /> : <HamburgerIcon />}
            aria-label="Open Menu"
            display={{ md: 'none' }}
            onClick={() => setIsOpen(!isOpen)}
          />

          <HStack spacing={8} display={{ base: 'none', md: 'flex' }}>
            {['Products', 'Solutions', 'Pricing', 'Docs'].map((item) => (
              <MotionText
                key={item}
                cursor="pointer"
                _hover={{ color: 'blue.400' }}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.95 }}
              >
                {item}
              </MotionText>
            ))}
          </HStack>

          <Button
            as={motion.button}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            bg="blue.400"
            color="white"
            _hover={{
              bg: 'blue.500',
            }}
          >
            Get Started
          </Button>
        </Flex>

        <Collapse in={isOpen} animateOpacity>
          <Box pb={4} display={{ md: 'none' }}>
            <VStack spacing={4}>
              {['Products', 'Solutions', 'Pricing', 'Docs'].map((item) => (
                <Text key={item} cursor="pointer">
                  {item}
                </Text>
              ))}
            </VStack>
          </Box>
        </Collapse>

        <MotionFlex
          align="center"
          justify={{ base: 'center', md: 'space-between' }}
          direction={{ base: 'column', md: 'row' }}
          minH="70vh"
          px={8}
          mb={16}
        >
          <VStack spacing={6} align="flex-start" maxW="600px">
            <MotionHeading
              as="h2"
              size="3xl"
              fontWeight="bold"
              color="blue.400"
              textAlign="left"
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              Revolutionize Your Digital Landscape
            </MotionHeading>
            <MotionText
              fontSize="xl"
              color={useColorModeValue('gray.300', 'gray.300')}
              textAlign="left"
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
            >
              NexusTech empowers businesses with cutting-edge cloud solutions and AI-driven insights. Stay ahead in the digital race with our innovative platform.
            </MotionText>
            <MotionBox
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.4 }}
            >
              <Button
                as={motion.button}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                colorScheme="blue"
                size="lg"
                rightIcon={<FaRocket />}
              >
                Launch Your Project
              </Button>
            </MotionBox>
          </VStack>
          <Box w={{ base: '100%', md: '50%' }} mt={{ base: 12, md: 0 }}>
            <MotionBox
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.8 }}
            >
              <Box
                className="hero-graphic"
                h="400px"
                bg="linear-gradient(45deg, #00A3FF, #0047FF)"
                borderRadius="2xl"
                boxShadow="0 0 50px #0047FF"
                position="relative"
                overflow="hidden"
              >
                <Box
                  className="hero-graphic-overlay"
                  position="absolute"
                  top="0"
                  left="0"
                  right="0"
                  bottom="0"
                  bg="url('data:image/svg+xml,%3Csvg width='20' height='20' viewBox='0 0 20 20' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='%239C92AC' fill-opacity='0.2' fill-rule='evenodd'%3E%3Ccircle cx='3' cy='3' r='3'/%3E%3Ccircle cx='13' cy='13' r='3'/%3E%3C/g%3E%3C/svg%3E')"
                />
              </Box>
            </MotionBox>
          </Box>
        </MotionFlex>

        <Heading as="h3" size="xl" mb={8} textAlign="center">
          Our Core Features
        </Heading>
        <Flex flexWrap="wrap" justifyContent="space-between">
          <FeatureCard
            icon={FaCloud}
            title="Cloud Solutions"
            description="Scalable and secure cloud infrastructure for your growing business needs."
          />
          <FeatureCard
            icon={FaBrain}
            title="AI-Powered Insights"
            description="Harness the power of artificial intelligence to drive data-driven decisions."
          />
          <FeatureCard
            icon={FaLock}
            title="Advanced Security"
            description="State-of-the-art security measures to protect your valuable data."
          />
        </Flex>
      </Container>

      <style jsx global>{`
        .hero-graphic::before,
        .hero-graphic::after {
          content: '';
          position: absolute;
          top: -50%;
          left: -50%;
          width: 200%;
          height: 200%;
          background: conic-gradient(transparent, rgba(168, 239, 255, 0.1), transparent 30%);
          animation: rotate 6s linear infinite;
        }
        .hero-graphic::after {
          animation-duration: 10s;
        }
        @keyframes rotate {
          100% {
            transform: rotate(1turn);
          }
        }
      `}</style>
    </Box>
  );
}
