import { Camera, Video } from "lucide-react";
import React, { useEffect, useRef } from "react";
import { render } from "react-dom";
import { motion, useAnimation, useInView } from "framer-motion";
const LandingSection = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, {
    once: true,
  });
  const mainControls = useAnimation();
  const secondaryControls = useAnimation();
  useEffect(() => {
    if (isInView) {
      mainControls.start("visible");
      secondaryControls.start("visible");
    }
  }, [isInView, mainControls, secondaryControls]);
  return (
    <section
      ref={ref}
      className="w-full max-w-full min-h-screen flex flex-col justify-center items-center bg-gradient-to-b from-gray-900 to-black text-white overflow-hidden" >
      <div className="absolute top-10 left-10">
        <Camera className="text-gray-600" size={48} />
      </div>
      <motion.div
        variants={{
          hidden: {
            opacity: 0,
            y: 75,
          },
          visible: {
            opacity: 1,
            y: 0,
          },
        }}
        initial="hidden"
        animate={mainControls}
        transition={{
          duration: 0.5,
          delay: 0.25,
        }}
        className="text-center mb-8" >
        <h1 className="text-4xl md:text-6xl font-bold mb-4">
          Spend your time
        </h1>
      </motion.div>
      <motion.div
        variants={{
          hidden: {
            opacity: 0,
            y: 75,
          },
          visible: {
            opacity: 1,
            y: 0,
          },
        }}
        initial="hidden"
        animate={secondaryControls}
        transition={{
          duration: 0.5,
          delay: 0.75,
        }}
        className="text-center" >
        <motion.h2
          className="text-6xl md:text-8xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-green-400 to-green-600"
          animate={{
            scale: [1, 1.1, 1],
            rotate: [0, 5, -5, 0],
            textShadow: [
              "0px 0px 8px rgba(0, 255, 0, 0.7)",
              "0px 0px 16px rgba(0, 255, 0, 0.8)",
              "0px 0px 8px rgba(0, 255, 0, 0.7)",
            ],
          }}
          transition={{
            duration: 5,
            ease: "easeInOut",
            times: [0, 0.5, 1],
            repeat: Infinity,
            repeatDelay: 1,
          }} >
          EFFICIENTLY
        </motion.h2>
      </motion.div>
      <div className="absolute bottom-10 right-10">
        <Video className="text-gray-600" size={48} />
      </div>
    </section>
  );
};
export default LandingSection;
render(<LandingSection />, document.getElementById("root"));
