import React from 'react';
import {
  Box,
  Flex,
  Text,
  Button,
  VStack,
  HStack,
  Container,
  Heading,
  useColorModeValue,
} from '@chakra-ui/react';
import { motion } from 'framer-motion';

const MotionBox = motion(Box);

export default function Index2() {
  return (
    <Box bg="black" color="white" minH="100vh">
      <Container maxW="container.xl">
        <Flex as="nav" align="center" justify="space-between" wrap="wrap" padding="1.5rem" mb={8}>
          <Flex align="center" mr={5}>
            <Heading as="h1" size="lg" letterSpacing={'tighter'}>
              NexusTech
            </Heading>
          </Flex>

          <HStack spacing={8}>
            {['Products', 'Solutions', 'Pricing', 'Docs'].map((item) => (
              <Text key={item} cursor="pointer" _hover={{ color: 'blue.400' }}>
                {item}
              </Text>
            ))}
          </HStack>

          <Button
            bg="transparent"
            border="1px"
            borderColor="blue.400"
            color="blue.400"
            _hover={{
              bg: 'blue.400',
              color: 'white',
            }}
          >
            Get Started
          </Button>
        </Flex>

        <Flex
          align="center"
          justify={{ base: 'center', md: 'space-around', xl: 'space-between' }}
          direction={{ base: 'column-reverse', md: 'row' }}
          wrap="no-wrap"
          minH="70vh"
          px={8}
          mb={16}
        >
          <VStack spacing={4} align="flex-start" maxW="480px">
            <Heading
              as="h2"
              size="2xl"
              fontWeight="bold"
              color="blue.400"
              textAlign="left"
            >
              Accelerate Your Digital Transformation
            </Heading>
            <Text
              fontSize="xl"
              color={useColorModeValue('gray.300', 'gray.300')}
              textAlign="left"
            >
              NexusTech empowers businesses with cutting-edge cloud solutions and AI-driven insights. Stay ahead in the digital race.
            </Text>
            <Button
              colorScheme="blue"
              borderRadius="8px"
              py="4"
              px="4"
              lineHeight="1"
              size="md"
            >
              Explore Our Platform
            </Button>
          </VStack>
          <Box w={{ base: '80%', sm: '60%', md: '50%' }} mb={{ base: 12, md: 0 }}>
            <MotionBox
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.8 }}
            >
              {/* Replace this with your own futuristic 3D or animated graphic */}
              <Box
                className="hero-graphic"
                h="400px"
                bg="linear-gradient(45deg, #00A3FF, #0047FF)"
                borderRadius="2xl"
                boxShadow="0 0 50px #0047FF"
                position="relative"
                overflow="hidden"
              >
                <Box
                  className="hero-graphic-overlay"
                  position="absolute"
                  top="0"
                  left="0"
                  right="0"
                  bottom="0"
                  bg="url('data:image/svg+xml,%3Csvg width='20' height='20' viewBox='0 0 20 20' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='%239C92AC' fill-opacity='0.2' fill-rule='evenodd'%3E%3Ccircle cx='3' cy='3' r='3'/%3E%3Ccircle cx='13' cy='13' r='3'/%3E%3C/g%3E%3C/svg%3E')"
                />
              </Box>
            </MotionBox>
          </Box>
        </Flex>
      </Container>

      {/* Add this to your existing CSS or create a new CSS file and import it */}
      <style jsx global>{`
        .hero-graphic::before,
        .hero-graphic::after {
          content: '';
          position: absolute;
          top: -50%;
          left: -50%;
          width: 200%;
          height: 200%;
          background: conic-gradient(transparent, rgba(168, 239, 255, 0.1), transparent 30%);
          animation: rotate 6s linear infinite;
        }
        .hero-graphic::after {
          animation-duration: 10s;
        }
        @keyframes rotate {
          100% {
            transform: rotate(1turn);
          }
        }
      `}</style>
    </Box>
  );
}
